import React, { useState, useEffect } from 'react';
import { BarChart, Bar, LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, PieChart, Pie, Cell } from 'recharts';
import { getPrediction, getHistoricalData } from '../services/api';

// Mock data for demo purposes until real data is available
const usageData = [
  { month: '1月', 租借次数: 4000, 维修次数: 240 },
  { month: '2月', 租借次数: 3000, 维修次数: 210 },
  { month: '3月', 租借次数: 5000, 维修次数: 290 },
  { month: '4月', 租借次数: 7000, 维修次数: 350 },
  { month: '5月', 租借次数: 9000, 维修次数: 410 },
  { month: '6月', 租借次数: 8000, 维修次数: 390 },
];

const stationData = [
  { name: '江南站', value: 400 },
  { name: '明洞站', value: 300 },
  { name: '弘大站', value: 500 },
  { name: '梨泰院站', value: 200 },
  { name: '汝矣岛站', value: 280 },
];

const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042', '#8884D8'];

const BikeRentalDashboard = () => {
  const [bikeCount, setBikeCount] = useState(500);
  const [usageIntensity, setUsageIntensity] = useState(70);
  const [weatherFactor, setWeatherFactor] = useState(50);

  // Weather prediction related states
  const [temperature, setTemperature] = useState(25);
  const [humidity, setHumidity] = useState(60);
  const [precipitation, setPrecipitation] = useState(0);
  const [windSpeed, setWindSpeed] = useState(10);
  const [isHoliday, setIsHoliday] = useState(false);
  const [dayOfWeek, setDayOfWeek] = useState(1);
  const [season, setSeason] = useState(2); // Default to summer (1: spring, 2: summer, 3: fall, 4: winter)
  const [hour, setHour] = useState(new Date().getHours());
  const [workingday, setWorkingday] = useState(new Date().getDay() === 0 || new Date().getDay() === 6 ? 0 : 1);
  const [weather, setWeather] = useState(1); // Default to Clear weather (1: Clear, 2: Cloudy, 3: Light Rain, 4: Heavy Rain)

  // Prediction results
  const [predictedRentals, setPredictedRentals] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [predictionError, setPredictionError] = useState(null);

  // Prediction data for future days (will be updated after prediction)
  const [futurePredictions, setFuturePredictions] = useState([]);

  // Calculate maintenance workload
  const calculateMaintenanceLoad = () => {
    // Simple formula: bike count * usage intensity * weather factor / 10000
    const baseLoad = (bikeCount * usageIntensity * weatherFactor) / 10000;
    return {
      dailyInspections: Math.round(baseLoad * 20),
      weeklyMaintenance: Math.round(baseLoad * 5),
      monthlyOverhaul: Math.round(baseLoad * 1.2),
      staffNeeded: Math.ceil(baseLoad * 3)
    };
  };

  // Call model API
  const predictRentals = async () => {
    setIsLoading(true);
    setPredictionError(null);

    try {
      // Calculate working day based on day of week (0,6 are weekend)
      const isWorkingDay = (dayOfWeek !== 0 && dayOfWeek !== 6) ? 1 : 0;

      // Prepare parameters exactly as expected by the API
      const apiParams = {
        season: parseInt(season),
        month: parseInt(new Date().getMonth() + 1), // Current month (1-12)
        hour: parseInt(hour),
        holiday: isHoliday ? 1 : 0,
        weekday: parseInt(dayOfWeek),
        workingday: isWorkingDay,
        weather: parseInt(weather),
        temp: parseFloat(temperature),
        humidity: parseFloat(humidity),
        windspeed: parseFloat(windSpeed),
        // Add year parameter which is expected by the API
        year: 1
      };

      console.log('Sending API request with params:', apiParams);

      // Make the API call
      const response = await getPrediction(apiParams);
      console.log('API response:', response);

      // Handle the API response
      if (response && typeof response.prediction === 'number') {
        setPredictedRentals(response.prediction);

        // Generate future 7 days predictions data based on the current prediction
        const weekdays = ['周日', '周一', '周二', '周三', '周四', '周五', '周六'];
        const predictions = [];

        for (let i = 0; i < 7; i++) {
          const futureDay = (dayOfWeek + i) % 7;
          const dayName = weekdays[futureDay];
          const isWeekend = futureDay === 0 || futureDay === 6;
          const tempVariation = Math.random() * 5 - 2.5; // -2.5 to 2.5 random variation
          const rainChance = Math.random() > 0.7;
          const rainAmount = rainChance ? Math.random() * 20 : 0;

          const dailyBasePrediction = response.prediction * 0.9; // Base from the actual prediction
          const dailyTempFactor = (temperature + tempVariation) > 20 ? 
                                ((temperature + tempVariation) - 20) * 0.05 : 
                                ((temperature + tempVariation) - 20) * 0.03;
          const dailyRainFactor = rainAmount > 0 ? -rainAmount * 0.1 : 0;
          const dailyWeekendBonus = isWeekend ? 0.2 : 0;

          const variation = 1 + dailyTempFactor + dailyRainFactor + dailyWeekendBonus;
          const dailyPrediction = Math.max(0, Math.round(dailyBasePrediction * variation));

          predictions.push({
            day: dayName,
            date: `4/${3 + i}`,
            prediction: dailyPrediction,
            temperature: Math.round(temperature + tempVariation),
            precipitation: Math.round(rainAmount),
          });
        }

        setFuturePredictions(predictions);
      } else {
        throw new Error('Invalid API response format');
      }
    } catch (error) {
      console.error('Prediction failed:', error);
      setPredictionError('Failed to get prediction data, please try again later. Error: ' + (error.message || JSON.stringify(error)));
    } finally {
      setIsLoading(false);
    }
  };

  const maintenanceLoad = calculateMaintenanceLoad();

  // Calculate maintenance workload based on predicted rentals
  const calculatePredictedMaintenance = () => {
    if (predictedRentals === null) return null;

    const maintenanceFactor = predictedRentals / 1000;
    return {
      dailyInspections: Math.round(maintenanceFactor * 15),
      repairs: Math.round(maintenanceFactor * 3)
    };
  };

  const predictedMaintenance = calculatePredictedMaintenance();

  return (
    <div className="flex flex-col min-h-screen bg-gray-100">
      <header className="bg-blue-600 text-white p-4">
        <h1 className="text-2xl font-bold">首尔自行车租借系统 - 维护工作量计算仪表盘</h1>
      </header>

      <div className="flex-1 p-4">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
          <div className="bg-white p-4 rounded-lg shadow">
            <h3 className="text-lg font-semibold text-gray-700">总车辆数</h3>
            <p className="text-3xl font-bold">{bikeCount}</p>
          </div>
          <div className="bg-white p-4 rounded-lg shadow">
            <h3 className="text-lg font-semibold text-gray-700">日均租借次数</h3>
            <p className="text-3xl font-bold">2,543</p>
          </div>
          <div className="bg-white p-4 rounded-lg shadow">
            <h3 className="text-lg font-semibold text-gray-700">维修中车辆</h3>
            <p className="text-3xl font-bold">23</p>
          </div>
          <div className="bg-white p-4 rounded-lg shadow">
            <h3 className="text-lg font-semibold text-gray-700">维护工作人员</h3>
            <p className="text-3xl font-bold">{maintenanceLoad.staffNeeded}</p>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-6">
          <div className="bg-white p-4 rounded-lg shadow">
            <h3 className="text-lg font-semibold text-gray-700 mb-4">月度租借与维修情况</h3>
            <ResponsiveContainer width="100%" height={300}>
              <BarChart data={usageData}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="month" />
                <YAxis yAxisId="left" orientation="left" />
                <YAxis yAxisId="right" orientation="right" />
                <Tooltip />
                <Legend />
                <Bar yAxisId="left" dataKey="租借次数" fill="#8884d8" />
                <Bar yAxisId="right" dataKey="维修次数" fill="#82ca9d" />
              </BarChart>
            </ResponsiveContainer>
          </div>

          <div className="bg-white p-4 rounded-lg shadow">
            <h3 className="text-lg font-semibold text-gray-700 mb-4">热门站点分布</h3>
            <ResponsiveContainer width="100%" height={300}>
              <PieChart>
                <Pie
                  data={stationData}
                  cx="50%"
                  cy="50%"
                  labelLine={true}
                  outerRadius={100}
                  fill="#8884d8"
                  dataKey="value"
                  label={({name, percent}) => `${name} ${(percent * 100).toFixed(0)}%`}
                >
                  {stationData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                  ))}
                </Pie>
                <Tooltip />
              </PieChart>
            </ResponsiveContainer>
          </div>
        </div>

        {/* Weather-rental prediction module */}
        <div className="bg-white p-6 rounded-lg shadow mb-6">
          <h3 className="text-xl font-semibold text-gray-700 mb-4">天气-租借量预测模型</h3>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
            <div>
              <label className="block text-gray-700 mb-2">温度 (°C)</label>
              <input
                type="range"
                min="-10"
                max="40"
                value={temperature}
                onChange={(e) => setTemperature(parseInt(e.target.value))}
                className="w-full"
              />
              <div className="flex justify-between">
                <span>-10°C</span>
                <span className="font-semibold">{temperature}°C</span>
                <span>40°C</span>
              </div>
            </div>

            <div>
              <label className="block text-gray-700 mb-2">湿度 (%)</label>
              <input
                type="range"
                min="0"
                max="100"
                value={humidity}
                onChange={(e) => setHumidity(parseInt(e.target.value))}
                className="w-full"
              />
              <div className="flex justify-between">
                <span>0%</span>
                <span className="font-semibold">{humidity}%</span>
                <span>100%</span>
              </div>
            </div>

            <div>
              <label className="block text-gray-700 mb-2">降水量 (mm)</label>
              <input
                type="range"
                min="0"
                max="50"
                value={precipitation}
                onChange={(e) => setPrecipitation(parseInt(e.target.value))}
                className="w-full"
              />
              <div className="flex justify-between">
                <span>0mm</span>
                <span className="font-semibold">{precipitation}mm</span>
                <span>50mm</span>
              </div>
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
            <div>
              <label className="block text-gray-700 mb-2">风速 (m/s)</label>
              <input
                type="range"
                min="0"
                max="30"
                value={windSpeed}
                onChange={(e) => setWindSpeed(parseInt(e.target.value))}
                className="w-full"
              />
              <div className="flex justify-between">
                <span>0 m/s</span>
                <span className="font-semibold">{windSpeed} m/s</span>
                <span>30 m/s</span>
              </div>
            </div>

            <div>
              <label className="block text-gray-700 mb-2">星期几</label>
              <select 
                className="w-full p-2 border rounded" 
                value={dayOfWeek}
                onChange={(e) => setDayOfWeek(parseInt(e.target.value))}
              >
                <option value={0}>周日</option>
                <option value={1}>周一</option>
                <option value={2}>周二</option>
                <option value={3}>周三</option>
                <option value={4}>周四</option>
                <option value={5}>周五</option>
                <option value={6}>周六</option>
              </select>
            </div>

            <div>
              <label className="block text-gray-700 mb-2">是否节假日</label>
              <div className="flex items-center mt-4">
                <input
                  type="checkbox"
                  checked={isHoliday}
                  onChange={(e) => setIsHoliday(e.target.checked)}
                  className="h-5 w-5"
                />
                <span className="ml-2 text-gray-700">是节假日</span>
              </div>
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
            <div>
              <label className="block text-gray-700 mb-2">季节</label>
              <select 
                className="w-full p-2 border rounded" 
                value={season}
                onChange={(e) => setSeason(parseInt(e.target.value))}
              >
                <option value={1}>春季</option>
                <option value={2}>夏季</option>
                <option value={3}>秋季</option>
                <option value={4}>冬季</option>
              </select>
            </div>

            <div>
              <label className="block text-gray-700 mb-2">小时 (0-23)</label>
              <input
                type="number"
                min="0"
                max="23"
                value={hour}
                onChange={(e) => setHour(parseInt(e.target.value))}
                className="w-full p-2 border rounded"
              />
            </div>

            <div>
              <label className="block text-gray-700 mb-2">天气状况</label>
              <select 
                className="w-full p-2 border rounded" 
                value={weather}
                onChange={(e) => setWeather(parseInt(e.target.value))}
              >
                <option value={1}>晴朗</option>
                <option value={2}>多云</option>
                <option value={3}>小雨</option>
                <option value={4}>大雨</option>
              </select>
            </div>
          </div>

          <div className="flex justify-center mt-4 mb-6">
            <button 
              className="px-6 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500"
              onClick={predictRentals}
              disabled={isLoading}
            >
              {isLoading ? '预测中...' : '预测租借量'}
            </button>
          </div>

          {predictedRentals !== null && !predictionError && (
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div className="bg-blue-50 p-6 rounded-lg">
                <h4 className="text-xl font-semibold text-blue-800 mb-3">预测结果</h4>
                <div className="text-4xl font-bold text-blue-700">{predictedRentals} <span className="text-xl">次租借</span></div>
                <p className="mt-2 text-blue-600">基于当前天气条件的预测租借量</p>

                {predictedMaintenance && (
                  <div className="mt-4 grid grid-cols-2 gap-4">
                    <div className="bg-white p-3 rounded-md shadow-sm">
                      <h5 className="font-semibold text-gray-700">预计日检查</h5>
                      <p className="text-2xl font-bold">{predictedMaintenance.dailyInspections} 次</p>
                    </div>
                    <div className="bg-white p-3 rounded-md shadow-sm">
                      <h5 className="font-semibold text-gray-700">预计修理</h5>
                      <p className="text-2xl font-bold">{predictedMaintenance.repairs} 次</p>
                    </div>
                  </div>
                )}
              </div>

              <div>
                <h4 className="text-lg font-semibold text-gray-700 mb-3">预测影响因素分析</h4>
                <div className="bg-white p-4 rounded-lg shadow-sm">
                  <div className="flex justify-between items-center mb-2">
                    <span>温度影响</span>
                    <span className={temperature > 15 && temperature < 30 ? "text-green-600 font-bold" : "text-yellow-600 font-bold"}>
                      {temperature > 15 && temperature < 30 ? "适宜骑行" : "影响骑行意愿"}
                    </span>
                  </div>
                  <div className="flex justify-between items-center mb-2">
                    <span>降水影响</span>
                    <span className={precipitation > 0 ? "text-red-600 font-bold" : "text-green-600 font-bold"}>
                      {precipitation > 0 ? `降低约 ${Math.round(precipitation * 3)}%` : "无负面影响"}
                    </span>
                  </div>
                  <div className="flex justify-between items-center mb-2">
                    <span>时间影响</span>
                    <span className={(dayOfWeek === 0 || dayOfWeek === 6) ? "text-green-600 font-bold" : "text-blue-600 font-bold"}>
                      {(dayOfWeek === 0 || dayOfWeek === 6) ? "周末增加约15%" : "工作日标准"}
                    </span>
                  </div>
                  <div className="flex justify-between items-center">
                    <span>节假日影响</span>
                    <span className={isHoliday ? "text-green-600 font-bold" : "text-gray-600"}>
                      {isHoliday ? "增加约25%" : "无额外影响"}
                    </span>
                  </div>
                </div>
              </div>
            </div>
          )}

          {predictionError && (
            <div className="bg-red-50 p-4 rounded-lg text-red-700">
              <p className="font-semibold">{predictionError}</p>
            </div>
          )}

          {futurePredictions.length > 0 && (
            <div className="mt-6">
              <h4 className="text-lg font-semibold text-gray-700 mb-3">未来7天预测</h4>
              <ResponsiveContainer width="100%" height={300}>
                <LineChart data={futurePredictions}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="date" tickFormatter={(value, index) => `${value} ${futurePredictions[index].day}`} />
                  <YAxis />
                  <Tooltip content={({ active, payload, label }) => {
                    if (active && payload && payload.length) {
                      const data = payload[0].payload;
                      return (
                        <div className="bg-white p-3 border rounded shadow-sm">
                          <p className="font-semibold">{data.date} {data.day}</p>
                          <p>预测租借: <span className="font-bold">{data.prediction}</span> 次</p>
                          <p>温度: {data.temperature}°C</p>
                          <p>降水: {data.precipitation}mm</p>
                        </div>
                      );
                    }
                    return null;
                  }} />
                  <Legend />
                  <Line type="monotone" dataKey="prediction" stroke="#8884d8" strokeWidth={2} />
                </LineChart>
              </ResponsiveContainer>
            </div>
          )}
        </div>

        <div className="bg-white p-6 rounded-lg shadow mb-6">
          <h3 className="text-xl font-semibold text-gray-700 mb-4">维护工作量计算器</h3>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
            <div>
              <label className="block text-gray-700 mb-2">自行车总数量</label>
              <input
                type="range"
                min="100"
                max="2000"
                value={bikeCount}
                onChange={(e) => setBikeCount(parseInt(e.target.value))}
                className="w-full"
              />
              <div className="flex justify-between">
                <span>100</span>
                <span className="font-semibold">{bikeCount}</span>
                <span>2000</span>
              </div>
            </div>

            <div>
              <label className="block text-gray-700 mb-2">使用强度 (%)</label>
              <input
                type="range"
                min="10"
                max="100"
                value={usageIntensity}
                onChange={(e) => setUsageIntensity(parseInt(e.target.value))}
                className="w-full"
              />
              <div className="flex justify-between">
                <span>低 (10%)</span>
                <span className="font-semibold">{usageIntensity}%</span>
                <span>高 (100%)</span>
              </div>
            </div>

            <div>
              <label className="block text-gray-700 mb-2">天气因素</label>
              <input
                type="range"
                min="10"
                max="100"
                value={weatherFactor}
                onChange={(e) => setWeatherFactor(parseInt(e.target.value))}
                className="w-full"
              />
              <div className="flex justify-between">
                <span>良好 (10)</span>
                <span className="font-semibold">{weatherFactor}</span>
                <span>恶劣 (100)</span>
              </div>
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <div className="bg-blue-50 p-4 rounded-lg">
              <h4 className="font-semibold text-blue-700">日常检查次数</h4>
              <p className="text-2xl font-bold">{maintenanceLoad.dailyInspections}</p>
            </div>
            <div className="bg-green-50 p-4 rounded-lg">
              <h4 className="font-semibold text-green-700">每周维护次数</h4>
              <p className="text-2xl font-bold">{maintenanceLoad.weeklyMaintenance}</p>
            </div>
            <div className="bg-yellow-50 p-4 rounded-lg">
              <h4 className="font-semibold text-yellow-700">月度大修次数</h4>
              <p className="text-2xl font-bold">{maintenanceLoad.monthlyOverhaul}</p>
            </div>
            <div className="bg-purple-50 p-4 rounded-lg">
              <h4 className="font-semibold text-purple-700">建议工作人员</h4>
              <p className="text-2xl font-bold">{maintenanceLoad.staffNeeded} 人</p>
            </div>
          </div>
        </div>
      </div>

      <footer className="bg-gray-800 text-white p-4 text-center">
        首尔自行车租借系统管理平台 2025
      </footer>
    </div>
  );
};

export default BikeRentalDashboard;
